package com.example.courseapp.navigationsealed class Routes(val routes:String){    object Home:Routes("home")    object Calendar:Routes("calendar")    object AddCourse:Routes("addCourse")    object CourseDetail : Routes("courseDetail")    object Course: Routes("course")    object Lesson : Routes("lesson")    object Notification:Routes("notification")    object Profile:Routes("profile")    object Setting:Routes("setting")    object Splash:Routes("splash")    object BottomNav:Routes("bottomNav")    object Login:Routes("login")    object Register:Routes("register")    object Forgot:Routes("forgot")}