package com.example.courseapp.screens.componentimport androidx.compose.foundation.Imageimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.layout.sizeimport androidx.compose.foundation.shape.CircleShapeimport androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.outlined.Notificationsimport androidx.compose.material3.Iconimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.clipimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.layout.ContentScaleimport androidx.compose.ui.platform.LocalContextimport androidx.compose.ui.res.painterResourceimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport coil.compose.rememberAsyncImagePainterimport com.example.courseapp.Rimport com.example.courseapp.utils.SharePref@Composablefun HeaderScreen() {    val context = LocalContext.current    Row(horizontalArrangement = Arrangement.Center, verticalAlignment = Alignment.CenterVertically) {        Image(            painter = rememberAsyncImagePainter(model = SharePref.getImage(context)),            contentDescription =null,            Modifier                .clip(shape = CircleShape)                .size(50.dp),            contentScale = ContentScale.Crop        )        Spacer(modifier = Modifier.padding(end=12.dp) )        Text(            text = "Hi, "+ SharePref.getUserName(context),            fontSize = 20.sp,            fontWeight = FontWeight.Bold,            color = Color(0xFF2A3447)        )        Spacer(modifier = Modifier.weight(1f))        Icon(Icons.Outlined.Notifications, contentDescription = null, Modifier.size(24.dp) )    }}